import { useState } from "react";
import "../styles/Lecturer.css";

const Lecturer = () => {
  const [systemStatus, setSystemStatus] = useState("stopped"); // running / stopped

  const handleStart = () => setSystemStatus("running");
  const handleStop = () => setSystemStatus("stopped");

  return (
    <div className="lecturer-container">
      <h1 className="lecturer-title">Lecturer Dashboard</h1>

      {/* حالة النظام */}
      <div className={`system-status ${systemStatus}`}>
        System Status: {systemStatus === "running" ? "Running" : "Stopped"}
      </div>

      {/* أزرار التحكم */}
      <div className="buttons-wrapper">
        {systemStatus === "stopped" ? (
          <button className="action-btn start-btn" onClick={handleStart}>
            Start Monitoring
          </button>
        ) : (
          <button className="action-btn stop-btn" onClick={handleStop}>
            Stop Monitoring
          </button>
        )}

        <button className="action-btn report-btn">
          View Current Lecture Report
        </button>
        <button className="action-btn report-btn">View All Reports</button>
      </div>
    </div>
  );
};

export default Lecturer;
