import { useState } from "react";
import { useNavigate } from "react-router-dom";
import "../styles/lecturer.css";

const Lecturer = ({ onLogin }) => {
  const [role, setRole] = useState(""); // dropdown role
  const navigate = useNavigate();

  const handleLogin = (e) => {
    e.preventDefault();
    if (!role) return alert("Please select a role");
    onLogin(); // تحديث حالة تسجيل الدخول

    // إعادة التوجيه حسب الدور
    if (role === "lecturer") {
      navigate("/login"); // أو أي path صفحته المحاضر
    } else if (role === "admin") {
      navigate("/AdminDashboard"); // خلي عندك route للـ admin
    }
  };

  return (
    <div className="login-container">
      <div className="cards-wrapper">
        <div className="login-card">
          <div className="card-content">
            <h1 className="login-title">Login</h1>
            <form className="login-form" onSubmit={handleLogin}>
              <input className="login-input" placeholder="Email" />
              <input
                className="login-input"
                placeholder="Password"
                type="password"
              />

              <select
                className="login-input role-select"
                value={role}
                onChange={(e) => setRole(e.target.value)}
              >
                <option value="" disabled>
                  Role
                </option>
                <option value="lecturer">Lecturer</option>
                <option value="admin">Admin</option>
              </select>

              <button className="login-btn" type="submit">
                Login
              </button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Lecturer;
