import { useState } from "react";
import { useNavigate } from "react-router-dom";
import "../styles/lecturer.css";

const Lecturer = () => {
  const navigate = useNavigate();
  const [systemStatus, setSystemStatus] = useState("Stopped"); // حالة النظام

  // دوال الأزرار
  const startMonitoring = () => setSystemStatus("Running");
  const stopMonitoring = () => setSystemStatus("Stopped");

  const viewCurrentReport = () => {
    alert("عرض تقرير المحاضرة الحالي (يمكن تعديل ليكون صفحة حقيقية)");
  };

  const viewAllReports = () => {
    alert("عرض كل التقارير (يمكن تعديل ليكون صفحة حقيقية)");
  };

  const handleLogout = () => {
    navigate("/auth"); // رجوع للصفحة الرئيسية لتسجيل الدخول
  };

  return (
    <div className="lecturer-container">
      {/* Header */}
      <header className="lecturer-header">
        <h1>Welcome, Dr. Smith</h1>
        <button className="logout-btn" onClick={handleLogout}>
          Logout
        </button>
      </header>

      {/* الحالة */}
      <div className="system-status">
        <span>Status: </span>
        <span
          className={
            systemStatus === "Running" ? "status-running" : "status-stopped"
          }
        >
          {systemStatus}
        </span>
      </div>

      {/* أزرار التحكم */}
      <div className="control-buttons">
        <button onClick={startMonitoring} className="control-btn">
          Start Monitoring
        </button>
        <button onClick={stopMonitoring} className="control-btn">
          Stop Monitoring
        </button>
        <button onClick={viewCurrentReport} className="control-btn">
          View Current Lecture Report
        </button>
        <button onClick={viewAllReports} className="control-btn">
          View All Reports
        </button>
      </div>
    </div>
  );
};

export default Lecturer;
