import { useNavigate } from "react-router-dom";
import "../styles/dashboard.css"; // ðŸ‘ˆ Ù†ÙØ³ CSS Ø§Ù„Ø£Ø¯Ù…Ù†

const LectureReport = () => {
  const navigate = useNavigate();

  return (
    <div className="dashboard">
      {/* ================= Sidebar ================= */}
      <aside className="sidebar">
        <div className="logo">FocusMate</div>

        <ul>
          <li onClick={() => navigate("/Lecturer")}>Dashboard</li>
          <li className="active">Current Lecture Report</li>
          <li onClick={() => navigate("/auth")}>Logout</li>
        </ul>
      </aside>

      {/* ================= Main ================= */}
      <main className="main">
        {/* ================= Topbar ================= */}
        <div className="topbar">
          <h2>Current Lecture Report</h2>
        </div>

        {/* ================= Report Cards ================= */}
        <div className="cards">
          <ReportItem label="Lecturer Name" value="Dr. Smith" />
          <ReportItem label="Course Name" value="Artificial Intelligence" />
          <ReportItem label="Lecture Date" value="2026-01-16" />
          <ReportItem label="Lecture Duration" value="1h 30m" />
        </div>

        <div className="cards">
          <ReportItem label="Average Attention" value="78%" highlight />
          <ReportItem label="Highly Focused Students" value="22" />
          <ReportItem label="Fluctuating Attention Students" value="10" />
          <ReportItem label="Distracted Students" value="6" />
        </div>

        {/* ================= Charts ================= */}
        <div className="charts">
          <div className="card">
            <h3>Peak Attention Time</h3>
            <div className="chart-placeholder">00:25 â€“ 00:45</div>
          </div>

          <div className="card">
            <h3>Peak Distraction Time</h3>
            <div className="chart-placeholder">01:10 â€“ 01:25</div>
          </div>
        </div>
      </main>
    </div>
  );
};

/* ================= Reusable Card ================= */
const ReportItem = ({ label, value, highlight }) => {
  return (
    <div className={`card stat-card ${highlight ? "active" : ""}`}>
      <h2>{value}</h2>
      <p>{label}</p>
    </div>
  );
};

export default LectureReport;
